<?php/** * buttons/help2.php * * @package		J2XMLImporter * @subpackage	com_j2xmlimporter * @version		1.6.0 * @since		File available since Release v1.5.0 * * @author		Helios Ciancio <info@eshiol.it> * @link		http://www.eshiol.it * @copyright	Copyright (C) 2010 Helios Ciancio. All Rights Reserved * @license		http://www.gnu.org/licenses/gpl-3.0.html GNU/GPL v3 * J2XMLImporter is free software. This version may have been modified pursuant * to the GNU General Public License, and as distributed it includes or * is derivative of works licensed under the GNU General Public License or * other free or open source software licenses. */ // no direct accessdefined('_JEXEC') or die('Restricted access.');jimport('joomla.version');class JButtonHelp2 extends JButton{	/**	 * @var		string	Button type	 */	var $_name = 'Help';	/**	 * @param	string	$type		Unused string.	 * @param	string	$ref		The name of the help screen (its key reference).	 * @param	boolean	$site		URL of site to get Help.	 *	 * @return	string	 * @since	1.5	 */	public function fetchButton($type = 'Help', $name='', $text='', $ref = '', $site = 'http://www.eshiol.it')	{		$version = new JVersion();				$class	= $this->fetchIconClass('help2');		$i18n_text	= JText::_($text);		$html = "";				$html .= "<a href=\"#\" onclick=\"popupWindow('$site/index.php?option=com_content&task=findkey&tmpl=component&keyref=".$ref.$version->getHelpVersion()."&lang=en', 'Help', 800, 600, 1)\" rel=\"help\" class=\"toolbar\">\n";		$html .= "<span class=\"icon-32-help\" title=\"$i18n_text\">\n";		$html .= "</span>\n";		$html .= "$i18n_text\n";		$html .= "</a>\n";    	   		return $html;	}		/**	 * Get the button CSS Id	 *	 * @access	public	 * @return	string	Button CSS Id	 * @since	1.5	 */	public function fetchId($type='Help2', $name = '', $text = '', $task = '', $list = true, $hideMenu = false)	{		return $this->_getToolbarName().'-'.$name;	}	/**	 * Get the name of the toolbar.	 *	 * @return	string	 * @since	1.5	 */	private function _getToolbarName()	{		$version = new JVersion();		if ($version->RELEASE == '1.5')					return $this->_parent->_name;		else			return $this->_parent->getName();	}}?>